import react, {Fragment,useContext} from 'react'
import CartContext from '../../store/CartContext'
const Header = () =>{

    const carCtx = useContext(CartContext)

    const getCountItem = () =>{
        return carCtx.items.reduce( (count, current) =>  count + current.amount , 0) // < Start with 0
    }

    return <Fragment>

              <div>
                <span>ReactMeals</span>
                <button>{`$(getCountItem())`}``</button>
              </div>

           </Fragment>

}

export default Header
