import React, { useState, useEffect, useReducer } from 'react';

import Card from '../UI/Card/Card';
import classes from './Login.module.css';
import Button from '../UI/Button/Button';

const emailReducer = (prevState,action) =>{

  if(action.type === 'USER_INPUT'){
    return {value:action.value, isValid: action.value.includes('@')}
  }

  if(action.type === 'INPUT_BLUR'){
    return { value: prevState.value, isValid: prevState.value.includes('@')}
  }

  return {value:'', isValid: false}

}

const passwordReducer = (prevState,action) =>{

  if(action.type === 'PASSWORD_INPUT'){
    return {value:action.value, isValid: action.value.length > 6}
  }

  if(action.type === 'PASSWORD_BLUR'){
    return { value: prevState.value, isValid: prevState.value.length > 6}
  }

  return {value:'', isValid: false}

}

const Login = (props) => {

  // const [enteredEmail, setEnteredEmail] = useState('');
  // const [emailIsValid, setEmailIsValid] = useState();
  // const [enteredPassword, setEnteredPassword] = useState('');
  // const [passwordIsValid, setPasswordIsValid] = useState();
  const [formIsValid, setFormIsValid] = useState(false);

  const [emailState, dispatchEmail] = useReducer(emailReducer,{value:'', isValid: false},);
  const [passwordState, dispatchPassword] = useReducer(passwordReducer,{value:'', isValid: false},);

  useEffect(()=>{ 

                  const identifier = setTimeout( ()=>{setFormIsValid(passwordState.isValid && emailState.isValid)} , 500 )

                  return () => { clearTimeout(identifier); }

                }

                , [passwordState.value,emailState.value] );


  const emailChangeHandler = (event) => {

      dispatchEmail({type: 'USER_INPUT', value: event.target.value})

  };

  const passwordChangeHandler = (event) => {

    dispatchPassword({ type: 'PASSWORD_INPUT',  value: event.target.value });

  };

  const validateEmailHandler = () => {
      dispatchEmail({type: 'INPUT_BLUR'})
  };

  const validatePasswordHandler = () => {
       dispatchPassword()
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(emailState.value, enteredPassword);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            emailState.isValid === false ? classes.invalid : ''
          }`}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={emailState.value}
            onChange={emailChangeHandler}
            onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${
            passwordIsValid === false ? classes.invalid : ''
          }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={enteredPassword}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
