import react, {Fragment,useContext,useState,useEffect,useRef} from 'react'
import CartContext from '../../store/CartContext'

const Header = (props) =>{

    const carCtx = useContext(CartContext) 
    const buttonRef = useRef()
    const [countItem,setCountItem]  = useState(carCtx.items.reduce( (count, current) =>  count + current.amount , 0))
   
    useEffect(()=>{
        if(countItem>0)
            buttonRef. button.style.backgroundColor="green";
        else
            buttonRef.style.backgroundColor="green";
    },[countItem])
    
    const onClickCart =  () =>{

         props.showCartFlagHandler( true )
    }

    return <Fragment>

              <div>
                <span>ReactMeals</span>
                <button ref={buttonRef} onClick={onClickCart}>Your Cart  ({countItem} item)</button>
              </div>

           </Fragment>

}

export default Header
