import react, {Fragment,useContext,useState,useEffect,useRef} from 'react'
import CartContext from '../../store/CartContext'

const Header = (props) =>{

    const cartCtx = useContext(CartContext) 
    const [styleBtn,setStyleBtn] = useState({width: '25px',
                                            hight: '25'})

    const items = cartCtx.items

    useEffect(()=>{
        
              setStyleBtn({width: '25px',
                          hight: '25'});
        
              setStyleBtn("gray");
    },[items])
    
    const onClickCart =  () =>{

         props.showCartFlagHandler( true )
    }

    console.log(cartCtx)

    return <Fragment>

              <div>
                <span>ReactMeals</span>
                <button style={{width: `${styleBtn.width}`, height: `${styleBtn.height}`}} onClick={onClickCart}>Your Cart  ({cartCtx.totalAmount} item)</button>
              </div>

           </Fragment>

}

export default Header
