import react, {Fragment,useContext,useState,useEffect,useRef} from 'react'
import CartContext from '../../store/CartContext'

const Header = (props) =>{

    const carCtx = useContext(CartContext) 
    const [styleBtn,setStyleBtn] = useState('gray')

    useEffect(()=>{
        if(carCtx>0)
              setStyleBtn("green");
        else
              setStyleBtn("gray");
    },[countItem])
    
    const onClickCart =  () =>{

         props.showCartFlagHandler( true )
    }

    return <Fragment>

              <div>
                <span>ReactMeals</span>
                <button style={{backgroundColor: `${styleBtn}`}} onClick={onClickCart}>Your Cart  ({countItem} item)</button>
              </div>

           </Fragment>

}

export default Header
