import react, {Fragment,useContext,useEffect,useRef} from 'react'
import CartContext from '../../store/CartContext'

const Header = (props) =>{

    const carCtx = useContext(CartContext) 
    const buttonRef = useRef()
    const countItem = carCtx.items.reduce( (count, current) =>  count + current.amount , 0) // < Start with 0

    useEffect(()=>{
        buttonRef.style.background='gren'
    },[])
    
    const onClickCart =  () =>{

         props.showCartFlagHandler( true )
    }

    return <Fragment>

              <div>
                <span>ReactMeals</span>
                <button ref={buttonRef} onClick={onClickCart}>Your Cart  ({countItem} item)</button>
              </div>

           </Fragment>

}

export default Header
