const CartItem = (props) =>{

    const  item = {
        id: props.id,
        amount: 1,
        price: props.price
    }

    console.log( typeof props.subItemHandler)
    console.log( typeof props.addItemHandler)

    const onClickAddItem = () =>{

        console.log( typeof props.addItemHandler(item))
        props.addItemtHandler(item);

    }

    const onClickSubItem = () =>{

        props.subItemtHandler(item);

    }

    return <Fragment>
                <div>{props.nameMeal}</div>
                <div>x {props.amount}</div>
                <div>{props.totalPrice}</div>
                <button onClick={onClickAddItem}>+</button>
                <button onClick={onClickSubItem}>-</button>
            </Fragment>

}

