import react,{Fragment} from 'react'

const CartItem = (props) =>{

    const  item = {
        id: props.id,
        amount: 1,
        price: props.price
    }

 
    const onClickAddItem = () =>{

        props.addItemHandler(item);

    }

    const onClickSubItem = () =>{

        props.subItemHandler(item);

    }

    return <Fragment>
                 <li> 
                    <div>{props.id}</div>
                    <div>x {props.amount}</div>
                    <div>{props.price}</div>
                    <button onClick={onClickAddItem}>+</button>
                    <button onClick={onClickSubItem}>-</button>
                </li> 
                <div>Total price: {props.totalPrice}</div>
                <div>
                    <button >order</button>
                </div>
            </Fragment>

}

export default CartItem
