import react,{Fragment,useReducer} from 'react'
import {findIdexInArrayObject} from '../../helpers/ArrayHelpers'
import CartItem from './CartItem.js'
import CartContext from '../../store/CartContext'
import CartProvider from './'

const Cart = () =>{

    return <CartContext.Provider value={{ addItemHandler: addItemHandler ,
                                          subItemHandler: subItemHandler} } >

              <ul>

              {cartState.items.map(item => ( < CartItem 
                                                                    key={item.id}
                                                                    id = {item.id}
                                                                    amount = {item.amount} 
                                                                    price = {item.price}
                                                                    addItemHandler = {addItemHandler} 
                                                                    subItemHandler = {subItemHandler}/>
                                            ) )}
             </ul>

             <div>Total price: {cartState.totalPrice}</div>
                <div>
                    <button>order</button>
                    <button>close</button>
            </div>

         </CartContext.Provider>

}

export default Cart
